import React from 'react';
import {Link} from 'react-router-dom';
import './feed.css'
const Feed = ({articles})=> {
    if(articles){
        return (
            <div>
                {articles.map((article, index)=> (
                    <div key= {index}>
                    <div className= 'globalfeed' key = {index}>
                        <Link to = '/profiles/${article.body}'>
                             {article.body}
                        </Link>
                    </div>
                    <div className= 'info'>
                        <Link to = {`/profiles/${article.updatedAt}`}></Link>
                    </div>
                    <span className= 'date'>{article.createdAt}</span>
                    <Link to = {`/articles/${article.slug}`} className= 'preview-link'>
                        <h1>{article.title}</h1>
                        <h3>{article.description}</h3>
                    </Link>
                    </div>
                ))}
            </div>
            )
    }
    
}
export default Feed